# coding: utf-8

import base64
from io import BytesIO
from fontTools.ttLib import TTFont
from utils.constants import PATTTERN_MAP



class Unfont():
    def __init__(self, font_str):
        bin_data = base64.decodebytes(font_str.encode())
        font = TTFont(BytesIO(bin_data))
        self.cmp = font['cmap'].tables[0].ttFont.tables['cmap'].tables[0].cmap

    def __call__(self, show):
        ret = ""
        for s in show:
            try:
                # lst = [ord(s) for s in show]
                # ret_list = [str(int(self.cmp[ord(s)][-2:]) - 1) for decode_num in lst]
                real = str(int(self.cmp[ord(s)][-2:]) - 1)
            except:
                real = s
            ret += real
        return ret




if __name__ == '__main__':
    base64_str = ''
    price_code = '麣齤龒龒元'
    font = Unfont(base64_str)
    real = font(price_code)
    print(real)
